import React, { useState, useMemo } from 'react';
import { BrowserRouter as Router, Route, Switch, Redirect, useHistory } from 'react-router-dom';
import { ThemeProvider, createTheme, CssBaseline } from '@mui/material';
import LoginPage from './pages/LoginPage';
import HomePage from './pages/HomePage';
import axios from 'axios';

function App() {
  const [mode, setMode] = useState('light');
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Check if user is authenticated when the component mounts
  React.useEffect(() => {
    const checkAuthStatus = async () => {
      try {
        // In a real app with Django backend, check authentication status
        // This endpoint should be created in your Django project
        const response = await axios.get('/api/auth/status/', {
          withCredentials: true, // This is crucial for cookie-based authentication
        });

        setIsAuthenticated(response.data.isAuthenticated);
      } catch (error) {
        console.error('Error checking authentication:', error);
        setIsAuthenticated(false);
      }
    };

    checkAuthStatus();
  }, []);

  // Create theme based on current mode
  const theme = useMemo(
    () =>
      createTheme({
        palette: {
          mode,
          primary: {
            main: mode === 'light' ? '#1976d2' : '#90caf9',
          },
          secondary: {
            main: mode === 'light' ? '#dc004e' : '#f48fb1',
          },
          background: {
            default: mode === 'light' ? '#f5f5f5' : '#121212',
            paper: mode === 'light' ? '#ffffff' : '#1e1e1e',
          },
        },
        shape: {
          borderRadius: 8,
        },
        typography: {
          fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
          h4: {
            fontWeight: 600,
          },
        },
      }),
    [mode],
  );

  const toggleTheme = () => {
    setMode((prevMode) => (prevMode === 'light' ? 'dark' : 'light'));
  };

  // For demonstration, simulate a login and redirect
  const handleLogin = () => {
    localStorage.setItem('authToken', 'demo-token');
    setIsAuthenticated(true);

    // Force a redirect to the home page
    window.location.href = '/';
  };

  // Protected route component
  const ProtectedRoute = ({ children, ...rest }) => {
    return (
      <Route
        {...rest}
        render={({ location }) =>
          isAuthenticated ? (
            children
          ) : (
            <Redirect
              to={{
                pathname: "/login",
                state: { from: location }
              }}
            />
          )
        }
      />
    );
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Router>
        <Switch>
          <Route path="/login">
            {isAuthenticated ? (
              <Redirect to="/" />
            ) : (
              <LoginPage 
                toggleTheme={toggleTheme} 
                mode={mode} 
                onLoginSuccess={handleLogin}
              />
            )}
          </Route>

          <ProtectedRoute path="/dashboard">
            {/* Dashboard component will be added later */}
            <div>Dashboard Coming Soon</div>
          </ProtectedRoute>

          <ProtectedRoute path="/" exact>
            <HomePage toggleTheme={toggleTheme} mode={mode} />
          </ProtectedRoute>

          <Route path="*">
            <Redirect to="/" />
          </Route>
        </Switch>
      </Router>
    </ThemeProvider>
  );
}

export default App;
