import React, { useState, useEffect, useMemo } from 'react';
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom';
import { ThemeProvider, createTheme, CssBaseline } from '@mui/material';
import LoginPage from './pages/LoginPage';
import HomePage from './pages/HomePage';
import axios from 'axios';

// Configure axios to include credentials
axios.defaults.withCredentials = true;

function App() {
  const [mode, setMode] = useState('light');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [userData, setUserData] = useState(null);
  const [authError, setAuthError] = useState(null);

  useEffect(() => {
    const checkAuthStatus = async () => {
      try {
        // Use the real API endpoint
        const response = await axios.get('/api/user-info/');

        setIsAuthenticated(response.data.isAuthenticated);
        setAuthError(null);

        if (response.data.isAuthenticated) {
          setUserData({
            email: response.data.email,
            role: response.data.role
          });
        }
      } catch (error) {
        console.error('Authentication check failed:', error);
        setAuthError('API Error: ' + error.message);
        setIsAuthenticated(false);

        // Only for development - comment out or remove for production
        if (process.env.NODE_ENV === 'development' && false) { // Set to true to use mock data
          console.log('Development mode: Using mock data for testing');
          setIsAuthenticated(true);
          setUserData({
            email: 'dev@example.com',
            role: 'student'
          });
        }
      } finally {
        setIsLoading(false);
      }
    };

    checkAuthStatus();
  }, []);

  // Theme settings - keep as is
  const theme = useMemo(
    () =>
      createTheme({
        palette: {
          mode,
          primary: {
            main: mode === 'light' ? '#1976d2' : '#90caf9',
          },
          secondary: {
            main: mode === 'light' ? '#dc004e' : '#f48fb1',
          },
          background: {
            default: mode === 'light' ? '#f5f5f5' : '#121212',
            paper: mode === 'light' ? '#ffffff' : '#1e1e1e',
          },
        },
        shape: {
          borderRadius: 8,
        },
        typography: {
          fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
          h4: {
            fontWeight: 600,
          },
        },
      }),
    [mode],
  );

  const toggleTheme = () => {
    setMode((prevMode) => (prevMode === 'light' ? 'dark' : 'light'));
  };

  const ProtectedRoute = ({ children, ...rest }) => {
    if (isLoading) {
      return <div>Loading...</div>;
    }

    return (
      <Route
        {...rest}
        render={({ location }) =>
          isAuthenticated ? (
            children
          ) : (
            <Redirect
              to={{
                pathname: "/login",
                state: { from: location }
              }}
            />
          )
        }
      />
    );
  };

  if (isLoading) {
    return <div>Loading authentication status...</div>;
  }

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Router>
        <Switch>
          <Route path="/login">
            {isAuthenticated ? (
              <Redirect to="/" />
            ) : (
              <LoginPage 
                toggleTheme={toggleTheme} 
                mode={mode} 
                authError={authError}
              />
            )}
          </Route>

          <ProtectedRoute path="/dashboard">
            <div>Dashboard Coming Soon</div>
          </ProtectedRoute>

          <ProtectedRoute path="/" exact>
            <HomePage 
              toggleTheme={toggleTheme} 
              mode={mode} 
              userData={userData} 
            />
          </ProtectedRoute>

          <Route path="*">
            <Redirect to="/" />
          </Route>
        </Switch>
      </Router>
    </ThemeProvider>
  );
}

export default App;
