import React, { useState, useEffect, useMemo } from 'react';
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom';
import { ThemeProvider, createTheme, CssBaseline, Box, Typography, Alert } from '@mui/material';
import LoginPage from './pages/LoginPage';
import HomePage from './pages/HomePage';
import axios from 'axios';

function App() {
  const [mode, setMode] = useState('light');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [connectionError, setConnectionError] = useState(null);

  useEffect(() => {
    const checkAuthStatus = async () => {
      try {
        // Try with the correct URL explicitly to bypass proxy issues
        const response = await axios.get('http://127.0.0.1:8000/api/auth/status/', {
          withCredentials: true,
        });

        setIsAuthenticated(response.data.isAuthenticated);
        setConnectionError(null);
      } catch (error) {
        console.error('Error checking authentication:', error);
        setIsAuthenticated(false);

        if (error.code === 'ERR_NETWORK' || error.code === 'ECONNREFUSED') {
          setConnectionError("Cannot connect to the Django backend. Please ensure it's running at http://127.0.0.1:8000");
        } else {
          setConnectionError("Authentication error: " + (error.response?.data?.detail || error.message));
        }
      } finally {
        setIsLoading(false);
      }
    };

    checkAuthStatus();
  }, []);

  const theme = useMemo(
    () =>
      createTheme({
        palette: {
          mode,
          primary: {
            main: mode === 'light' ? '#1976d2' : '#90caf9',
          },
          secondary: {
            main: mode === 'light' ? '#dc004e' : '#f48fb1',
          },
          background: {
            default: mode === 'light' ? '#f5f5f5' : '#121212',
            paper: mode === 'light' ? '#ffffff' : '#1e1e1e',
          },
        },
        shape: {
          borderRadius: 8,
        },
        typography: {
          fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
          h4: {
            fontWeight: 600,
          },
        },
      }),
    [mode],
  );

  const toggleTheme = () => {
    setMode((prevMode) => (prevMode === 'light' ? 'dark' : 'light'));
  };

  const ProtectedRoute = ({ children, ...rest }) => {
    if (isLoading) {
      return (
        <Box display="flex" justifyContent="center" alignItems="center" padding={4}>
          <Typography>Loading...</Typography>
        </Box>
      );
    }

    // For development purposes, if there's a connection error, still show the content
    // in a real app, you'd want to redirect to login
    if (connectionError) {
      return (
        <Route
          {...rest}
          render={() => (
            <>
              <Box sx={{ p: 2, mb: 2 }}>
                <Alert severity="warning">{connectionError}</Alert>
                <Typography variant="body2" sx={{ mt: 1 }}>
                  Showing content in development mode without authentication.
                </Typography>
              </Box>
              {children}
            </>
          )}
        />
      );
    }

    return (
      <Route
        {...rest}
        render={({ location }) =>
          isAuthenticated ? (
            children
          ) : (
            <Redirect
              to={{
                pathname: "/login",
                state: { from: location }
              }}
            />
          )
        }
      />
    );
  };

  if (isLoading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" padding={4}>
        <Typography>Loading authentication status...</Typography>
      </Box>
    );
  }

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      {connectionError && (
        <Box sx={{ position: 'fixed', top: 0, left: 0, right: 0, zIndex: 9999 }}>
          <Alert severity="error" sx={{ borderRadius: 0 }}>
            {connectionError}
          </Alert>
        </Box>
      )}
      <Router>
        <Switch>
          <Route path="/login">
            {isAuthenticated ? (
              <Redirect to="/" />
            ) : (
              <LoginPage 
                toggleTheme={toggleTheme} 
                mode={mode} 
                connectionError={connectionError}
              />
            )}
          </Route>

          <ProtectedRoute path="/dashboard">
            <div>Dashboard Coming Soon</div>
          </ProtectedRoute>

          <ProtectedRoute path="/" exact>
            <HomePage toggleTheme={toggleTheme} mode={mode} />
          </ProtectedRoute>

          <Route path="*">
            <Redirect to="/" />
          </Route>
        </Switch>
      </Router>
    </ThemeProvider>
  );
}

export default App;
